import React, { Component } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import "./styles/App.css";
import Navbar from "./components/Navbar";
import VideoDescription from "./components/VideoDescription";
import Upload from "./components/Upload";
import Axios from "axios";

const apiKey = "9646c732-3af7-4647-8da4-20fb64fd3c53";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      playlist: [],
      currentVideo: { comments: [] }
    };
  }

  setCurrentVideo = id => {
    Axios.get(
      `https://project-2-api.herokuapp.com/videos/${id}/?api_key=${apiKey}`
    )
      .then(data => {
        this.setState({
          currentVideoID: id,
          currentVideo: data.data
        });
      })
      .catch(error => console.log(error));
  };

  componentDidMount() {
    Axios.get(`https://project-2-api.herokuapp.com/videos/?api_key=${apiKey}`)
      .then(data => {
        let playlist = data.data;
        let currentVideoID = this.props.match.params.id;

        Axios.get(
          `https://project-2-api.herokuapp.com/videos/${currentVideoID}/?api_key=${apiKey}`
        ).then(data => {
          this.setState({
            playlist: playlist,
            currentVideo: data.data
          });
        });
      })
      .catch(error => {
        console.log(error);
      });
  }

  componentDidUpdate(prevProps, prevState) {
    console.log("componentDidUpdate fired");
    console.log("previous props are", prevProps);
    console.log("previous state is", prevState);
  }

  render() {
    return (
      <Router>
        <Navbar />
        <Switch>
          <Route
            path="/video/:id"
            render={renderProps => {
              return (
                <VideoDescription
                  currentVideo={this.state.currentVideo}
                  playlist={this.state.playlist}
                  currentVideoID={this.state.currentVideoID}
                  setCurrentVideo={this.setCurrentVideo}
                  {...renderProps}
                />
              );
            }}
          />
          <Route path="/upload" component={Upload} />
        </Switch>
      </Router>
    );
  }
}

export default App;
