let comments = [
  {
    name: "Michael Lyons",
    date: "12/18/2018",
    comment:
      "They BLEW the ROOF off at their last show, once everyone started figuring out they were going.This is still simply the greatest opening of a concert I have EVER witnessed."
  },
  {
    name: "Gary Wong",
    date: "12/12/2018",
    comment:
      "Every time I see him shred I feel so motivated to get off my couch and hop on my board.He’s so talented! I wish I can ride like him one day so I can really enjoy myself!"
  },
  {
    name: "Theodore Duncan",
    date: "11/15/2018",
    comment:
      "How can someone be so good!!! You can tell he lives for this and loves to do it every day.Everytime I see him I feel instantly happy! He’s definitely my favorite ever!"
  }
];

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      playlist: [],
      currentVideoID: "",
      currentVideo: {}
    };
  }
  state = {
    playlist: [],
    currentVideoID: ""
  };

  componentDidMount() {
    Axios.get(`https://project-2-api.herokuapp.com/videos/?api_key=${apiKey}`)
      .then(data => {
        let currentVideo = {};

        Axios.get(
          `https://project-2-api.herokuapp.com/videos/${data.data[0].id}/?api_key=${apiKey}`
        ).then(data => {
          console.log(data.data);
          currentVideo = data.data;
        });

        this.setState({
          playlist: data.data,
          currentVideoID: data.data[0].id,
          currentVideo: currentVideo
        });

        console.log(currentVideo);
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    return (
      <Router>
        <Navbar />
        <Switch>
          <Route path="/" exact>
            {/* <Video playlist={this.playlist[0]} /> */}
            <VideoDescription
              comments={comments}
              playlist={this.state.playlist}
              currentVideoID={this.state.currentVideoID}
            />
          </Route>
          <Route path="/upload">
            <Upload />
          </Route>
        </Switch>
      </Router>
    );
  }
}

export default App;
